PROC PANEL <BTWNG> <BTWNT> <CORRB> <COVB> <DASILVA> <DATA=SAS-data-set> <FIXONE> <FIXONETIME> <FIXTWO> <FULLER> <M=number> <NOINT> <NOPRINT> <OUTCORR> <OUTCOV> <OUTEST=SAS-data-set> <OUTTRANS=SAS-data-set> <PARKS> <PHI> <PLOTS=ACTSURFACE | ALL | FITPLOT... > <POOLED> <RANONE> <RANTWO> <RHO> <SINGULAR=number> <VCOMP=FB | NL | WH... >;     
	BY variables ;

	CLAG <OUT= SAS-data-set> ;

	CLASS <out= SAS-data-set> ;

	FLATDATA BASE=(variable, variable, ..., variable) INDID=variable KEEP=(variable, variable, ..., variable) ...;

	ID cross-section-id time-series-id ;

	INSTRUMENTS <CONSTANT> <CORRELATED=(variable, variable, ..., variable)> <DEPVAR> ...;

	LAG <OUT= SAS-data-set> ;

	MODEL <ARTEST=integer> <ATOL=number> <BANDOPT=<TRAILING | CENTERED | LEADING>> ...;

	OUTPUT <OUT=SAS-data-set> <PREDICTED=name> <RESIDUAL=name> ...;

	RESTRICT "string" equation <,equation2...> ;

	SLAG <OUT= SAS-data-set> ;

	TEST <ALL> <LM> <WALD> ...;

	XLAG <OUT= SAS-data-set> ;

	ZLAG <OUT= SAS-data-set> ;
