PROC PCA <COVARIANCE>
                <DATA=CAS-libref.data-table>
                <METHOD=EIG | ITERGS | NIPALS>
                <N=number>
                <NOINT>
                <OUTSTAT=CAS-libref.data-table>
                <PARPREFIX=name | PPREFIX=name | RPREFIX=name>
                <PLOTS=ALL | EIGEN | PATTERNPROFILE... >
                <PREFIX=name>
                <SINGULAR=p | SING=p |>
                <STANDARD>
                <VARDEF=DF | N | WEIGHT... >;                     
	BY variables ;


	CODE <COMMENT>
                    <FILE=filename>
                    <FORMATWIDTH=width> ...;

	DISPLAY <CASESENSITIVE>
                    <EXCLUDE>
                    <EXCLUDEALL> ...;

	DISPLAYOUT <NOREPLACE>
                    <REPEATED> ;

	FREQ variable ;

	OUTPUT <COPYVAR=variable | COPYVARS=(variables)>
                    <H< =prefix >>
                    <OUT=CAS-libref.data-table> ...;

	PARTIAL variables ;

	VAR variables ;

	WEIGHT variable ;
