PROC PHREG <ALPHA=number> <ATRISK> <COVM> <COVOUT> <COVSANDWICH <(AGGREGATE)>> <DATA=SAS-data-set> <INEST=SAS-data-set> <MULTIPASS> <NAMELEN=n> <NOPRINT> <NOSUMMARY> <OUTEST=SAS-data-set> <PLOTS=CUMHAZ | MCF | NONE... > <SIMPLE>;     
	ABORT <ABEND> <CANCEL> <CANCEL FILE> ...;     
	ARRAY <_ALL_> <_NUMERIC_> <_TEMPORARY_> ...; 

	ASSESS <NPATHS=n> PROPORTIONALHAZARDS VAR=(variable-list) ...;     
	BASELINE COVARIATES=SAS-data-set OUT=SAS-data-set OUTDIFF=SAS-data-set ...;     
	BAYES <COEFFPRIOR=<UNIFORM | NORMAL | ZELLNER>> <DIAGNOSTICS=<ALL | NONE | AUTOCORR>... > <DISPERSIONPRIOR=<GAMMA | IGAMMA | IMPROPER>> ...; 

	BY <DESCENDING> <NOTSORTED> ; 

	CALL routine(parameter-1<, ...parameter-n>); 

	CLASS <CPREFIX=n> <DESCENDING> <LPREFIX=n> ...; 

	CONTRAST <ALPHA=p> <E> <SINGULAR=number> ...; 

	DO <OVER> <> <> ...; 

	EFFECT EFFECT-NAME=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	ELSE <DO> <IF> <THEN> ...; 

	END;     
	ESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	FREQ <NOTRUNCATE> ; 

	GOTO label;     
	HAZARDRATIO <ALPHA=number> <AT (variable=ALL | REF | list <...variable=ALL | REF| list> )> <CL=<WALD | PL | BOTH>> ...; 

	ID variables ;     
	IF <DO> <THEN> <WHEN> ...; 

	LINK statement-label;     
	LSMEANS <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	LSMESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	MODEL <ABSFCONV=value | CONVERGELIKE=value> <ALPHA=number> <BEST=n> ...; 

	OUTPUT ATRISK= DFBETA= OUT=SAS-data-set ...; 

	PUT <_PAGE_> ;  

	RANDOM <ABSPCONV=r> <ALPHA=value> <DIST=<GAMMA | LOGNORMAL>> ...; 

	RETURN;     
	; 

	SLICE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	STORE <LABEL='label'> OUT=item-store-name ;     
	STRATA <MISSING> ; 

	TEST <AVERAGE> <E> <PRINT> ...; 

	WEIGHT <NORMALIZE> ;
