PROC PHSELECT <ABSCONV=r | ABSTOL=r> <ABSFCONV= r <n> | ABSFTOL=r <n>> <ABSGCONV=r <n> | ABSGTOL=r <n>> <ABSXCONV=r <n> | ABSXTOL=r <n>> <ALPHA=number> <CORRB> <COVB> <DATA=CAS-libref.data-table> <FCONV2=r <n> | FTOL2=r <n>> <FCONV=r <n> | FTOL=r <n>> <GCONV2=r <n> | GTOL2=r <n>> <GCONV=r <n> | GTOL=r <n>> <HESSIAN> <ITHIST> <LASSORHO=r> <LASSOSTEPS=n> <LASSOTOL=r> <LOGLIKENULL> <MAXFUNC=n | MAXFU=n> <MAXITER=n | MAXIT=n> <MAXTIME=r> <MINITER=n | MINIT=n> <NOCLPRINT< =number >> <NORMALIZE=YES | NO> <NOSTDERR> <PARTFIT> <TECHNIQUE=CONGRA | DBLDOG | LEVMAR... > <XCONV=r | XTOL=r>;     
	BY variables; 

	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...; 

	CODE <COMMENT> <CUMHAZ> <FILE=filename> ...; 

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...; 

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...; 

	EFFECT <DETAILS> NAME=COLLECTION|MULTIMEMBER|MM|POLYNOMIAL|POLY...  <NOEFFECT> ...;     
	FREQ variable ;     
	MODEL <CLB> <ENTRY=variable> <INCLUDE=n | single-effect | (effects)> ...;     
	OUTPUT <COPYVAR=variable | COPYVARS=(variables)> CUMHAZ= <OUT=CAS-libref.data-table> ...; 

	PARTITION <FRACTION(< TEST=fraction > < VALIDATE=fraction > < SEED=number >)> <ROLE=|ROLEVAR=variable (< TEST='value' > < TRAIN='value' > < VALIDATE='value' >)> ;     
	SELECTION <ADAPTIVE <(GAMMA=nonnegative number)>> <CHOOSE=<AIC | AICC | SBC>> <METHOD=<BACKWARD | FORWARD | FORWARDSWAP>... > ...; 

	STRATA <MISSING> ; 

	WEIGHT variable;
