PROC PLSMOD <CENSCALE> <CVTEST <(cvtest-options)>> <DATA=CAS-libref.data-table> <DETAILS> <METHOD=PLS | SIMPLS | PCR... > <NFAC=number> <NOCENTER> <NOCLPRINT<=number>> <NOCVSTDIZE> <NOSCALE> <VARSS>;     
	BY <DESCENDING> <NOTSORTED> ;

	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...;

	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...;

	DISPLAYOUT <NOREPLACE> <REPEATED> ;

	EFFECT EFFECT-NAME=COLLECTION|MULTIMEMBER|MM|POLYNOMIAL|POLY...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;

	MODEL <INTERCEPT> <SOLUTION> ;

	OUTPUT <COPYVAR=variable | COPYVARS=(variables)> <H> <OUT=CAS-libref.data-table> ...;

	PARTITION <FRACTION( <TEST=fraction> <SEED=number> )> <ROLEVAR | ROLE=variable (<TEST='value'> <TRAIN='value'>)> ;
