PROC PROBIT <C=rate> <CVOUT> <DATA=SAS-data-set> <GOUT=graphics-catalog> <HPROB=p> <INEST=SAS-data-set> <INVERSECL> <LACKFIT> <LOG> <LOG10> <NAMELEN=n> <NOPRINT> <OPTC> <ORDER=DATA | FORMATTED | FREQ... > <OUTEST=SAS-data-set> <PLOTS=ALL | CDFPLOT | IPPPLOT... > <XDATA=SAS-data-set>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CDFPLOT <ANNOTATE=SAS-data-set> <CAXIS=color> <VAR=variable> ...; 

	CLASS variables ;     
	EFFECTPLOT BOX CONTOUR FIT ...;     
	ESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	INSET <CFILL=color> <CFILLH=color> <CFRAME=color> ...; 

	IPPPLOT <ANNOTATE=SAS-data-set> <CAXIS=color> <VAR=variable> ...; 

	LPREDPLOT <ANNOTATE=SAS-data-set> <CAXIS=color> <VAR=variable> ...; 

	LSMEANS <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	LSMESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	MODEL <AGGREGATE=variable-list> <ALPHA=value> <CONVERGE=value> ...; 

	OUTPUT OUT=SAS-data-set PROB=name STD= ...; 

	PREDPPLOT <ANNOTATE=SAS-data-set> <CAXIS=color> <VAR=variable> ...; 

	SLICE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	STORE <LABEL='label'> ; 

	TEST <CHISQ> <DDF=value-list> <E> ...; 

	WEIGHT variable;
