PROC QUANTREG <ALGORITHM=SIMPLEX | INTERIOR | SMOOTH> <ALPHA=value> <CI=NONE | RANK | SPARSITY... > <DATA=SAS-data-set> <INEST=SAS-data-set> <NAMELEN=n> <ORDER=DATA | FORMATTED | FREQ... > <OUTEST=SAS-data-set> <PLOTS=ALL | DDPLOT | FITPLOT... > <PP>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS variables ;     
	EFFECT EFFECT-NAME=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	ESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	ID variables ;     
	MODEL <CORRB> <COVB> <CUTOFF=value> ...; 

	OUTPUT <COLUMNWISE> LEVERAGE= OUT=SAS-data-set ...;     
	PERFORMANCE <CPUCOUNT=<<1-1024> | ACTUAL>> <DETAILS> <THREADS> ...; 

	TEST <LR> <QINTERACT> <WALD> ...; 

	WEIGHT variable;
