PROC QUANTSELECT <DATA=SAS-data-set> <MAXMACRO=n> <NPRINT> <OUTDESIGN<(options)=><SAS-data-set>> <PLOTS=ALL | ACL|ACLPLOT | COEF|COEFFICIENTS|COEFFICIENTPANEL... > <SEED=number> <TESTDATA=SAS-data-set> <VALDATA=SAS-data-set>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <DELIMITER=’c’> <DESCENDING> <SHOW> ...; 

	EFFECT <EFFECT-NAME>=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	MODEL <DETAILS=<ALL | SUMMARY>> <HIERARCHY=<NONE | SINGLE | SINGLECLASS>> <NOINT> ...; 

	OUTPUT <OUT=SAS-data-set> <PREDICTED=> <RESIDUAL=> ...; 

	PARTITION <FRACTION(<TEST=fraction> <VALIDATE=fraction>)> <ROLEVAR= | ROLE=variable(<TEST=value> <TRAIN=value> <VALIDATE=value>)> ; 

	WEIGHT variable ;
