PROC REG <ALL> <ALPHA=number> <ANNOTATE=SAS-data-set> <CORR> <COVOUT> <DATA=SAS-data-set> <EDF> <GOUT=graphics-catalog> <LINEPRINTER> <NOPRINT> <OUTEST=SAS-data-set> <OUTSEB> <OUTSSCP=SAS-data-set> <OUTSTB> <OUTVIF> <PCOMIT=list> <PLOTS=ADJRSQ | AIC | ALL... > <PRESS> <RIDGE=list> <RSQUARE> <SIMPLE> <SINGULAR=n> <TABLEOUT> <USSCP>;     
	ADD variables ;

	BY <DESCENDING> <NOTSORTED> ;

	DELETE variables ;

	FREQ variable ;

	ID variables ;

	MODEL <ACOV> <ACOVMETHOD=<0 | 1 | 2>... > <HCCMETHOD=<0 | 1 | 2>... > ...;

	MTEST <CANPRINT> <DETAILS> <MSTAT=<FAPPROX | EXACT>> ...;

	OUTPUT <COOKD=> <COVRATIO=> <OUT=SAS-data-set> ...;

	PAINT ALLOBS <NOLIST> <RESET> ...;

	PRINT <ACOV> <ANOVA> <MODELDATA> ...;

	REFIT ;

	RESTRICT equation <, ..., equation> ;

	REWEIGHT <ALLOBS> <NOLIST> <RESET> ...;

	TEST <PRINT> ;

	VAR variables ;

	WEIGHT variable;
