PROC RELIABILITY <DATA=SAS-data-set> <GOUT=graphics-catalog> <NAMELEN=n>;     
	ANALYZE <CONFIDENCE=number> <CONVERGE=value> <CONVH=number> ...;

	BY variable-names ;

	CLASS variable-names ;

	DISTRIBUTION <BINOMIAL> <EXPONENTIAL> <EXTREME> ...;

	FMODE <LEGEND=> <NOLEGEND> <PLOTMODES> ...;

	FREQ variable-name ;

	INSET <CFILL=color | BLANK> <CFILL=BLANK> <CFILLH=color> ...;

	LOGSCALE <INITIAL=number list> <INTERCEPT=number < INTINIT >> ;

	MAKE <'ConvergenceStatus'> <'CorrMat'> OUT=SAS-data-set ...;

	MCFPLOT <CAXIS=color> <CCENSOR=color> <CENBIN> ...;

	MODEL <CONFIDENCE=number> <CONVERGE=value> <CONVH=number> ...;

	NENTER variable ;

	PROBPLOT <CAXIS=color> <CCENSOR=color> <CENBIN> ...;

	RELATIONPLOT <CAXIS=color> <CCENSOR=color> <CENBIN> ...;

	UNITID variable;
