PROC SEVSELECT <COVOUT> <CRITERION=AD | AIC | AICC... > <DATA=CAS-libref.data-table> <EMPIRICALCDF=AUTOMATIC | KAPLANMEIER | MODIFIEDKM... > <INEST=CAS-libref.data-table> <INITSAMPLE (initsample-option) | INITSAMPLE (initsample-option . . . initsample-option)> <NOCLPRINT< =number >> <NOPRINT> <OBJECTIVE=symbol-name> <OUTEST=CAS-libref.data-table> <OUTMODELINFO=SAS-data-set> <OUTSTAT=SAS-data-set> <OUTSTORE=store-name> <PRINT=ALL | ALLFITSTATS | CONVSTATUS... > <SELECTNLOTECH=CONGRA | DBLDOG | NEWRAP... > <SELECTOUT> <VARDEF=DF | N> <ZEROEST>;     
	ABORT <ABEND> <CANCEL> <CANCEL FILE> ...;     
	ARRAY <_ALL_> <_NUMERIC_> <_TEMPORARY_> ...; 

	ATTRIB <FORMAT=format> <INFORMAT=informat> <LABEL='label'> ...; 

	BY variable-list ;     
	CALL routine(parameter-1<, ...parameter-n>);     
	CLASS <DESCENDING> <MISSING> <ORDER=<FORMATTED | FREQ | INTERNAL>> ...; 

	CONTINUE;     
	DELETE; 

	DESCRIBE;     
	DISPLAY <CASESENSITIVE> <EXCLUDE> <EXCLUDEALL> ...; 

	DISPLAYOUT <INCLUDEALL> <NOREPLACE> <REPEATED> ...; 

	DIST _ALL_ _PREDEFINED_ _USER_ ...; 

	DO <OVER> <> <> ...; 

	;     
	; 

	DROP variable-list;     
	EFFECT <DETAILS> NAME=COLLECTION|MULTIMEMBER|MM|POLYNOMIAL|POLY...  <NOEFFECT> ...;     
	ELSE <DO> <IF> <THEN> ...; 

	END;     
	ENDRSUBMIT; 

	ERROR <message>;     
	EXECUTE; 

	FILE <DISK> <DUMMY> <LOG> ...; 

	FORMAT <DEFAULT=default-format> ; 

	GO TO label | GOTO label;     
	IF <DO> <THEN> <WHEN> ...; 

	INFILE <DATALINES4> <DATALINES> <DISK> ...; 

	INFORMAT <DEFAULT= default-informat> ; 

	INPUT <specification(s)><@|@@>; 

	KEEP variable-list;     
	; 

	LEAVE;     
	LENGTH <DEFAULT=n> ; 

	LINK label;     
	LIST; 

	LOCK <CLEAR> <LIST> ; 

	LOSS <LEFTCENSORED=variable-name LEFTCENSORED=number> <LEFTTRUNCATED=variable-name <(left-truncation-option)> LEFTTRUNCATED=number <(left-truncation-option)>> <RIGHTCENSORED=variable-name RIGHTCENSORED=number> ...; 

	MERGE <ALTER=> <BUFNO=<n | nK | hexX>... > <END=variable> ...; 

	MISSING character(s);     
	MODIFY <END=variable> <KEY=index> <NOBS=variable> ...; 

	NLOPTIONS <ABSCONV=r> <ABSFCONV=r[n]> <ABSGCONV=r[n]> ...; 

	OTHERWISE <DO> ; 

	OUTPUT <COPYVARS=variable-list> <FUNCTIONS=(function<(arg)><=variable> <function<(arg)><=variable>> …)> <OUT=CAS-libref.data-table> ...; 

	OUTSCORELIB <COMMONPACKAGE> <OUTBYID=SAS-data-set> OUTLIB=fcmp-library-name ...;     
	PAGE;     
	PUT <_ALL_> <_INFILE_> <_ODS_> ...; 

	PUTLOG 'message';     
	RENAME old-name-1=new-name-1 ... <old-name-n=new-name-n>; 

	REPLACE <data-set-name-1><...data-set-name-n>; 

	RETAIN <element-list(s) <initial-value(s) |; 

	RETURN;     
	RSUBMIT <options>; ENDRSUBMIT <CANCEL>;     
	SCALEMODEL <DFMIXTURE=<FULL | MEAN | QUANTILE>... > <INCLUDE=n | INCLUDE=single-effect | INCLUDE=(effects)> <INFORMATIVE> ...; 

	;     
	SELECTION <ADAPTIVE <(GAMMA=nonnegative number)>> <CHOOSE=<AIC | AICC | SBC>> <METHOD=<BACKWARD | FORWARD | FORWARDSWAP>... > ...; 

	SET <END=variable> <INDSNAME=variable> <KEY=index</UNIQUE>> ...; 

	SKIP <n>;     
	STOP; 

	WEIGHT variable-name ;     
	WHEN <DO> ; 

	WHERE where-expression-1;
