PROC SGMAP  MAPDATA=map-data-set  <PLOTDATA=plot-data-set > <MAPRESPDATA=response-data-set>
	<options>; 
	STYLEATTRS  </options>
	 CHOROMAP   <response-variable>  </options>;
	ESRIMAP  URL='map-service';
	OPENSTREETMAP ;
	BUBBLE  X=variable Y=variable SIZE=numeric-variable </options>;
	SCATTER  X=variable Y=variable </options>;
	SERIES  X=variable Y=variable </options>;
	TEXT  X=variable Y=variable TEXT=variable </options>;
	GRADLEGEND  <"name" > </options>;
	KEYLEGEND  <"name-1" "name-2"  ...> </options>;
	ATTRIB  variable-list(s)  attribute-list(s);
	BY  <DESCENDING> variable-1
		< ... <DESCENDING> variable-n>
		<NOTSORTED>;
	FORMAT  variable-1 < ...variable-n>  <format-1>  <variable-1< ...variable-n><format-2>>  ... <DEFAULT=default-format>;
	LABEL  variable-1=label-1 | ' ' <variable-n=label-n | ' '>  ...;
	WHERE  where-expression;
