PROC SGPANEL <options>;  
	PANELBY variable(s)</options>;
	STYLEATTRS  </options>
	BAND X=variable  Y=variable 
		UPPER=numeric-value |  numeric-variable | 
		 LOWER=numeric-value numeric-variable </options>;
	BLOCK X=category-variable   BLOCK=block-variable  </options>;
	BUBBLE X=variable Y=variable SIZE=numeric-variable</options>;
	DENSITY response-variable</options>;
	DOT category-variable<options>;
	DROPLINE X=variable |  x-axis-value | 
		Y=variable y-axis-value</options>;
	ELLIPSE X=numeric-variable Y=numeric-variable</options>;
	ELLIPSEPARM SEMIMAJOR=numeric-value |  numeric-variable |  
		SEMIMINOR=numeric-value numeric-variable </options>
	FRINGE numeric-variable </options>;
	GRADLEGEND <"name"></options>;
	HBAR category-variable</options>;
	HBARBASIC category-variable</options>;
	HBARPARM CATEGORY=category-variable RESPONSE=numeric-variable</options>;
	HBOX analysis-variable</options>;
	HEATMAP X=variable Y=variable</options>;
	HEATMAPPARM X=variable Y=variable
		COLORGROUP=variable | COLORRESPONSE=numeric-variable</options>;
	HIGHLOW  X=variable |  Y=variable 
		HIGH=numeric-variable LOW=numeric-variable</option(s)>;
	HISTOGRAM response-variable</options>;
	HLINE category-variable</options>;
	INSET variable< ...variable-n></options>
	KEYLEGEND <"name-1"  ... "name-n"></options>;
	LEGENDITEM TYPE=type NAME="text-string"</options>;
	LINEPARM X=numeric-value | numeric-variable | 
		Y=numeric-valuenumeric-variable | 
		 SLOPE=numeric-value numeric-variable
		 </option(s)>;
	LOESS  X=numeric-variable Y=numeric-variable</options>;
	NEEDLE  X=variable Y=numeric-variable</options>;
	PBSPLINE  X=numeric-variable Y=numeric-variable </options>;
	POLYGON X=x-variable Y=y-variable ID=id-variable</options>;
	REFLINE value(s)</options>;
	REG X=numeric-variable Y=numeric-variable </options>;
	SCATTER X=variable Y=variable</options>;
	SERIES X=variable Y=variable</options>;
	SPLINE X=variable Y=variable</options>;
	STEP X=variable Y=variable</options>;
	SYMBOLCHAR  NAME=identifier
		 CHAR="hex-string" | keyword   </options>;
	SYMBOLIMAGE NAME=identifier IMAGE="image-file-specification"</options>;
	TEXT X=variable Y=variable TEXT=variable</options>;
	VBAR category-variable</options>;
	VBARBASIC category-variable</options>;
	VBARPARM CATEGORY=category-variable RESPONSE=numeric-variable</options>;
	VBOX analysis-variable</options>;
	VECTOR X=numeric-variable Y=numeric-variable</options>;
	VLINE category-variable</options>;
	COLAXIS <options>;
	COLAXISTABLE variable< ...variable-n></options>;
	ROWAXIS <options>;
	ROWAXISTABLE variable< ...variable-n></options>;
	ATTRIB  variable-list(s) attribute-list(s);
	BY variable-1< ...variable-N><options>;
	FORMAT variable-1< ...variable-N<format-1>><options>;
	LABEL variable-1=label-1< ...variable-n=label-n>;
	WHERE where-expression;

HEATMAPPARM X=variable Y=variable
	 COLORGROUP=variable</options>;HEATMAPPARM X=variable Y=variable 
