PROC SGSCATTER <options>;             
	COMPARE X=variable |  (variable-1 ...variable-n) |  
		 Y=variable (variable-1 ...variable-n)  </options>;

	MATRIX variable-1 variable-2< ...variable-n></options>;

	PLOT plot-request(s)</options>;

	ATTRIB  variable-list(s) attribute-list(s);

	BY variable-1< ...variable-N><options>;

	FORMAT variable-1< ...variable-N<format-1>><options>;

	LABEL variable-1=label-1< ...variable-n=label-n>;

	WHERE where-expression;
