PROC SIMILARITY <DATA=SAS-data-set> <ORDER=> <OUT=SAS-data-set> <OUTMEASURE=SAS-data-set> <OUTPATH=SAS-data-set> <OUTSEQUENCE=SAS-data-set> <OUTSUM=INPUT | INPUTTARGET | TARGET... > <PLOTS=ALL | COSTS | DISTANCES... > <PRINT=DESCSTATS | PATHS | COSTS... > <PRINTDETAILS> <SEASONALITY=integer> <SORTNAMES>;     
	BY <DESCENDING> <NOTSORTED> ;

	FCMPOPT <QUIET=<ON | OFF>> <TRACE=<ON | OFF>> ;

	ID ACCUMULATE=NONE|TOTAL|AVERAGE|AVG...  ALIGN=BEGINNING|MIDDLE|ENDING  END=date | datetime ...;

	INPUT <ACCUMULATE=<NONE | TOTAL | AVERAGE>... > <DIF=(numlist)> <NORMALIZE=<NONE | ABSOLUTE | STANDARD>... > ...;

	TARGET <ACCUMULATE=<NONE | TOTAL | AVERAGE>... > <COMPRESS=<GLOBALABS= | GLOBALPCT= | LOCALABS>... > <DIF=(numlist)> ...;
