PROC SIMLIN <DATA= SAS-data-set> <EST= SAS-data-set> <ESTPRINT> <INTERIM= n> <NOPRINT> <NORED> <OUTEST= SAS-data-set> <START= n> <TOTAL> <TYPE= value>;     
	BY variables ;

	ENDOGENOUS variables ;

	EXOGENOUS variables ;

	ID variables ;

	LAGGED lag-var endogenous-var number ellipsis ;

	OUTPUT <OUT= SAS-data-set> <PREDICTED= names> <RESIDUAL= names> ...;
