PROC SPDO  LIBRARY=libref;
	ADD ACL <acl <acl2 ...>><C=cat T=type></options>;
	CLUSTER ADD cluster-tablename MEMBER=member-name1<MEMBER=member-name2...>;
	CLUSTER CREATE cluster-tablename MEMBER=member-name1<MEMBER=member-name2...><options>;
	CLUSTER DESTROY cluster-tablename;
	CLUSTER FIX  MEMBER=member-name;
	CLUSTER LIST cluster-tablename</VERBOSE><OUT=SAS-dataset-name>;
	CLUSTER MODIFY cluster-tablename MINMAXVARLIST=(var1var2 ...);
	CLUSTER REMOVE cluster-tablename MEMBER=member-name1
		<MEMBER=member-name2 ...>;
	CLUSTER REPLACE cluster-tablename OLDMEMBER=member-name
		NEWMEMBER=member-name;
	CLUSTER UNDO cluster- tablename;
	CONSTRAINT ADD table-name  WHERE where-clause-expression; 
	CONSTRAINT DESCRIBE  table-name;
	CONSTRAINT REMOVE  table-name;
	DELETE ACL acl<acl2 ...> | _ALL_<C=cat T=type></options>;
	LIST ACL acl<acl2 ...> | _ALL_<C=cat T=type></options>;
	LIST USERS ;
	LIST USERS/LOCKING ;
	MODIFY ACL <acl | _ALL_><C=cat T=type></options>;
	OPER <CANCEL | DISCONNECT | INTERRUPT>;
	QUIT; 
	REFRESH DOMAINS ;
	REFRESH PARMS ;
	SET ACLTYPE type;
	SET ACLUSER name; 
	SET MODE OPER ;
	SET USER user-ID<port-number>;
	SET USER/LOCKING user-ID threadID=number;
	SHOWLIBNAME _ALL_ | libref</options>;
	SPDSCMD  'command';
	TRUNCATE table-name;
