PROC SPP <DATA=SAS-data-set> <DESCENDING> <NAMELEN=number> <PLOTS=ALL | HISTOGRAM | NONE>;     
	BY <DESCENDING> <NOTSORTED> ; 

	COVTEST <TEST=<D | CM>> ; 

	MODEL <CENSCALE> <CL<(alpha-value)>> <CORRB> ...; 

	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r <n> | ABSFTOL=r<n>> <ABSGCONV= | ABSGTOL=r <n>> ...;     
	PARMS <PARMSDATA=SAS-data-set | PDATA=SAS-data-set> ;     
	PROCESS <AREA=> <EVENT=> <MARK=> ...; 

	TREND name = FIELD(field-definition);
