	CONNECT TO  dbms-name <AS alias>
		 <(connect-statement-argument-1=value-1 
		<connect-statement-argument-2=value-2 ...>)> 
		 <(database-connection-argument-1=value-1 
		<database-connection-argument-2=value-2 ...>)>;
