PROC SSM <BREAKPEAKS> <DATA=SAS-data-set> <NOPRINT> <OPTIMIZER( <TECHNIQUE=technique> <MAXITER=integer>)> <PLOTS=ALL | AO | MAXSHOCK... > <PRINTALL> <STATEINFO> <ZSPARSE>;     
	ABORT <ABEND> <CANCEL> <CANCEL FILE> ...;     
	ARRAY specification <...> ;     
	BY variables; 

	CALL routine(parameter-1<, ...parameter-n>); 

	COMPONENT <PRINT=<FILTER | SMOOTH>> ; 

	DELETE;     
	DEPLAG name(response-variable) lag-term1 <lag-term2 …> ;     
	DO <OVER> <> <> ...; 

	ELSE <DO> <IF> <THEN> ...; 

	END;     
	EVAL <PRINT=<FILTER | SMOOTH>> ; 

	GOTO label;     
	ID <INTERVAL=value> ; 

	IF <DO> <THEN> <WHEN> ...; 

	IRREGULAR <PRINT=SMOOTH> <VARIANCE=variable | number> ;     
	LINK statement-label; 

	MODEL <PRINT=<FILTER | SMOOTH>> ; 

	;     
	OUTPUT <ALPHA=number> <AO(<ALPHA=number> <MAXNUM=number> <MAXPCT=number>)> <BREAK(<ALPHA=number> <MAXNUM=number> <MAXPCT=number>)> ...; 

	PARMS <LOWER=(number1 number2 …) | LOWER=(number)> <UPPER=(number1 number2 …) | UPPER=(number)> ;     
	PUT <_PAGE_> ; 

	RETURN;     
	; 

	STATE <A1(nd)> <CHECKBREAK<( ELEMENTWISE | OVERALL)>> <COV( D ) <= (var1 var2 …) | (number1 number2 …)>COV( G ) <= (var1 var2 …) | (number1 number2 …)>COV( I ) <= (variable) | (number)>COV( RANK=integer )> ...;  

	STOP ;     
	TREND <AR= φ1 φ2 …φp> <CHECKBREAK<( ELEMENTWISE | OVERALL)>> <CROSS=(var1, var2, …) | CROSS(MATCHPARM)=(var1, var2, …)> ...; 

	WHEN <DO> ;
