	FORM variable value ... ;

	ID variable ;

	INITIAL <F(row,column)=value> <G(row,column)=value> ;

	RESTRICT <F (row,column)= value> <G (row,column)= value> ;

	VAR variable (difference, difference, ...) ... ;
