PROC SURVEYIMPUTE <DATA=SAS-data-set> <METHOD=FEFI | HOTDECK|HD> <NDONORS=r> <NOPRINT> <ORDER=DATA | FORMATTED | FREQ... > <SEED=number> <VARMETHOD=BRR | JACKKNIFE|JK | NONE>;     
	BY <DESCENDING> <NOTSORTED> ;

	CELLS variables ;

	CLASS <DESCENDING> <ORDER=<DATA | FORMATTED | FREQ>... > <TRUNCATE <=n>> ...;

	CLUSTER variables;

	ID variable;

	IMPJOINT <variables>;

	OUTPUT <DONORID<=name>> <OUT=SAS-data-set> <OUTJKCOEFS=SAS-data-set> ...;

	REPWEIGHTS variables;

	STRATA variables;

	VAR variables;

	WEIGHT variable;
