PROC SURVEYLOGISTIC <ALPHA=α> <DATA=SAS-data-set> <MISSING> <NOMCAR> <ORDER=DATA | FORMATTED | FREQ... > <RATE=value | SAS-data-set> <TOTAL=value | SAS-data-set> <TRUNCATE> <VARMETHOD=BRR | JACKKNIFE | TAYLOR>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CLASS <CPREFIX=n> <DESCENDING> <LPREFIX=n> ...; 

	CLUSTER variables ;     
	DOMAIN variables <variable*variable variable*variable*variable ... > ;     
	EFFECT EFFECT-NAME=COLLECTION|LAG|MULTIMEMBER|MM...  <PERIOD=variable> <WITHIN=(variables) | WITHIN=variable> ...;     
	ESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	FREQ variable ;     
	LSMEANS <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	LSMESTIMATE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | SCHEFFE | SIDAK>... > <ALPHA=number> ...; 

	MODEL DESCENDING EVENT=<’category’>|FIRST|LAST  ORDER=DATA|FORMATTED|FREQ...  ...;     
	OUTPUT <LOWER=name> <OUT=SAS-data-set> <PREDICTED=> ...; 

	REPWEIGHTS <DF=df> <JKCOEFS=jackknife-coefficient-specification> ; 

	SLICE <ADJDFE=<SOURCE | ROW>> <ADJUST=<BON | DUNNETT | NELSON>... > <ALPHA=number> ...; 

	STORE <LABEL='label'> ; 

	STRATA <LIST> <NOCOLLAPSE> ; 

	TEST <PRINT> ;  

	UNITS <DEFAULT=list> ; 

	WEIGHT variable;
