PROC SYSLIN <2SLS> <3SLS> <ALL> <ALPHA=value> <CONVERGE=value> <DATA=SAS-data-set> <FIML> <FIRST> <IT3SLS> <ITPRINT> <ITSUR> <K=value> <LIML> <MAXITER=n> <MELO> <NOINCLUDE> <NOPRINT> <OLS> <OUT=SAS-data-set> <OUTCOV3> <OUTCOV> <OUTEST=SAS-data-set> <OUTSSCP=SAS-data-set> <REDUCED> <SDIAG> <SIMPLE> <SINGULAR=value> <SUR> <USSCP> <USSCP2> <VARDEF=DF | N | WEIGHT... >;     
	BY variables ;

	ENDOGENOUS variables ;

	IDENTITY equation ;

	INSTRUMENTS variables ;

	MODEL <ALL> <ALPHA=value> <CORRB> ...;

	OUTPUT <PREDICTED=variable> <RESIDUAL=variable> ;

	RESTRICT equation , ..., equation ;

	SRESTRICT equation , ..., equation ;

	STEST <PRINT> ;

	TEST <PRINT> ;

	VAR variables ;

	WEIGHT variable ;
