PROC TIMESERIES <CROSSPLOTS=SERIES | CCF | ALL> <DATA= SAS-data-set> <MAXERROR= number> <OUT= SAS-data-set> <OUTCORR= SAS-data-set> <OUTCROSSCORR= SAS-data-set> <OUTDECOMP= SAS-data-set> <OUTSEASON= SAS-data-set> <OUTSUM= SAS-data-set> <OUTTREND= SAS-data-set> <PLOTS=SERIES | RESIDUAL | CYCLES... > <PRINT=DECOMP | SEASONS | DESCSTATS... > <PRINTDETAILS> <SEASONALITY= number> <SORTNAMES>;     
	BY <DESCENDING> <NOTSORTED> ;

	CORR <LAGS= (numlist)> <NLAG= number> <NPARMS= number> ...;

	CROSSCORR <LAGS= (numlist)> <NLAG= number> <TRANSPOSE=<NO | YES>> ...;

	CROSSVAR <ACCUMULATE=<NONE | TOTAL | AVERAGE>... > <DIF=( numlist )> <SDIF=( numlist )> ...;

	DECOMP <LAMBDA= number> <MODE=<ADD|ADDITIVE | MULT|MULTIPLICATIVE | LOGADD|LOGADDITIVE>... > <NPERIODS= number> ...;

	ID <ACCUMULATE=<NONE | TOTAL | AVERAGE>... > <ALIGN=<BEGINNING | MIDDLE | ENDING>> <END=date | datetime> ...;

	SEASON N NOBS <TRANSPOSE=<NO | YES>> ...;

	SPECTRA COS FREQ PERIOD ...;

	SSA <GROUPS=(numlist)...(numlist)> <LENGTH=number> <NPERIODS=number> ...;

	TREND NOBS <NPERIODS= number> <TRANSPOSE=<NO | YES>> ...;

	VAR <ACCUMULATE=<NONE | TOTAL | AVERAGE>... > <DIF=( numlist )> <SDIF=( numlist )> ...;
