PROC TSMODEL <AUXDATA=CAS-libref.data-table> <DATA=CAS-libref.data-table> <INOBJ=(object-name=CAS-libref.data-table ...)> <INSCALAR=CAS-libref.data-table> <LEAD=n> <LOGCONTROL=(severity=IGNORE | KEEP <severity=IGNORE | KEEP...>)> <OUT=CAS-libref.data-table> <OUTARRAY=CAS-libref.data-table> <OUTLOG=CAS-libref.data-table> <OUTOBJ=(object-name=CAS-libref.data-table ...)> <OUTSCALAR=CAS-libref.data-table> <OUTSUM=CAS-libref.data-table> <PUTTOLOG=YES | NO> <SEASONALITY=number>;     
	ARRAY <NOSYMBOLS> ; 

	ATTRIB <FORMAT=format-name> <LABEL='label'> <LENGTH=length> ...; 

	BY variables;     
	CALL routine(parameter-1<, ...parameter-n>);     
	CONTINUE; 

	DATALINES4;     
	DATALINES; 

	;     
	DELETE; 

	DELETEFUNC function-name;     
	DELETESUBR subroutine-name;     
	DESCRIBE; 

	DISPLAY <BELL> <BLANK> <NOINPUT> ...; 

	DO <OVER> <> <> ...; 

	DROP variable-list;     
	ELSE <DO> <IF> <THEN> ...; 

	END;     
	ENDRSUBMIT; 

	ENDSUB;     
	ENDSUBMIT ; 

	ERROR <message>;     
	EXECUTE; 

	FILE <DISK> <DUMMY> <LOG> ...; 

	FORMAT <DEFAULT=default-format> ; 

	FUNCTION <KIND= | GROUP='string'> <VARARGS> ; 

	GOTO label;     
	ID <ACCUMULATE=<NONE | TOTAL | AVERAGE>... > <ALIGN=<BEGINNING | MIDDLE | ENDING>> INTERVAL=DAY|HOUR|MINUTE...  ...;     
	IF <DO> <NOT> <THEN> ...; 

	INFORMAT <DEFAULT= default-informat> ; 

	INSCALARS scalar-name-list; | INSCALAR scalar-name-list; 

	KEEP variable-list;     
	KILLTASK _ALL_ |task1...taskn ;     
	LABEL variable='label'; 

	LEAVE;     
	LENGTH <DEFAULT=n> ; 

	LINK label;     
	LIST; 

	LISTFUNC function-name;     
	LISTSUBR subroutine-name;     
	LISTTASK <_ALL_|task> ;     
	LOCK <CLEAR> <LIST> ; 

	LOSTCARD;     
	MISSING character(s); 

	ODS DOCUMENT <ALL> <CLOSE> <EXCLUDE exclusion(s)| ALL | NONE> ...;     
	ODS ESCAPECHAR= 'escape-character';     
	ODS GRAPHICS <ANTIALIAS=<OFF | ON>> OFF ON ...;     
	ODS LISTING <EXCLUDE/SELECT ALL> <CLOSE> <EXCLUDE exclusion(s)| ALL | NONE> ...;     
	ODS MARKUP <ALL> <CLOSE> <EXCLUDE exclusion(s)| ALL | NONE> ...;     
	ODS NOPROCTITLE;     
	ODS NOUSEGOPT; 

	ODS OUTPUT <CLEAR> <CLOSE> <SHOW> ...; 

	ODS PRINTER <ALL> <CLOSE> <EXCLUDE exclusion(s)| ALL | NONE> ...;     
	ODS PROCLABEL 'string';     
	ODS PROCTITLE; 

	ODS RESULTS <OFF> <ON> ; 

	ODS RTF <ALL> <CLOSE> <EXCLUDE exclusion(s)| ALL | NONE> ...;     
	ODS TAGSETS.RTF <ALL> <CLOSE> <EXCLUDE exclusion(s)| ALL | NONE> ...;     
	ODS TEXT= 'text-string';     
	ODS TRACE <EXCLUDED> OFF ON ...;     
	ODS USEGOPT;     
	ODS VERIFY <ERROR> <OFF> <ON> ...; 

	;     
	OUTARGS out-argument-1, ..., out-argument-n;     
	OUTARRAYS array-name-list; | OUTARRAY array-name-list;     
	OUTPUT<data-set-name(s)>; 

	OUTSCALARS scalar-name-list; | OUTSCALAR scalar-name-list; 

	PAGE;     
	PRINT <OUTLOG> <WHERE where-clause>> ;     
	PUT <_ALL_> <_INFILE_> <_ODS_> ...; 

	PUTLOG 'message';     
	RDISPLAY <<CONNECTREMOTE=>server-ID >;     
	REDIRECT INPUT OUTPUT ;     
	REMOVE <data-set-name(s)>; 

	RENAME old-name-1=new-name-1 ... <old-name-n=new-name-n>; 

	REPLACE <data-set-name-1><...data-set-name-n>; 

	REQUIRE package-1 <package-2 ... package-n>;REQUIRE package (class-1 ...class-n);     
	RETAIN <element-list(s) <initial-value(s) |;     
	; 

	RGET <<CONNECTREMOTE=>server-ID>; 

	RSUBMIT <options>; ENDRSUBMIT <CANCEL>; 

	;     
	SIGNOFF <options>; 

	SIGNON <options> ;     
	SKIP <n>;     
	STOP;     
	STRUCT structure-name variable;     
	SUBMIT <DYNAMICS=(var-1=[number | "string"] <var-2=[number | "string"]...>)> <FILE=SAS-file-ref | 'File-path'> ;     
	SUBROUTINE <KIND= | GROUP='string'> <OUTARGS> <VARARGS> ...; 

	SYSTASK COMMAND "operating system command"; 

	VAR <ACCUMULATE=<NONE | TOTAL | AVERAGE>... > <SETMISSING=<MISSING | AVERAGE | MINIMUM>... > ; 

	WAITFOR<_ANY_ | _ALL_> taskname <taskname...><TIMEOUT=seconds>; 

	WHEN <DO> ;     
	WHERE where-expression-1;     
	WINDOW <COLOR=<BLACK | GRAY | PINK>... > <COLUMNS=columns> <ICOLUMN=column> ...;
