PROC UCM <DATA=SAS-data-set> <NOPRINT> <PLOTS=ALL | NONE | FILTER... > <PRINTALL>;     
	AUTOREG <NOEST=<RHO | VARIANCE | RHO VARIANCE)>> <PLOT=<FILTER | SMOOTH | (FILTER SMOOTH)>> <PRINT=<FILTER | SMOOTH | (FILTER SMOOTH)>> ...;

	BLOCKSEASON <BLOCKSIZE=integer> <NBLOCKS=integer> <NOEST> ...;

	BY variables ;

	CYCLE <NOEST=<PERIOD | RHO | VARIANCE>... > <PERIOD=value> <PLOT=<FILTER | SMOOTH | (FILTER SMOOTH)>> ...;

	DEPLAG <LAGS=order | LAGS=(lag, ..., lag ) ...(lag, ..., lag )> <NOEST> <PHI=value ...> ...;

	ESTIMATE <BACK=integer> <EXTRADIFFUSE=k> <NOPROFILE> ...;

	FORECAST <ALPHA=value> <BACK=integer> <EXTRADIFFUSE=k> ...;

	ID <ALIGN=<BEGINNING | MIDDLE | ENDING>> <INTERVAL=<DAY | HOUR | MINUTE>... > ;

	IRREGULAR <NOEST> <PLOT=<FILTER | SMOOTH | (<FILTER> <SMOOTH>)>> <PRINT=<FILTER | SMOOTH | (<FILTER> <SMOOTH>)>> ...;

	LEVEL <CHECKBREAK> <NOEST> <PLOT=<FILTER | SMOOTH | (<FILTER> <SMOOTH>)>> ...;

	MODEL dependent <= regressors> ;

	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r[n] | ABSFTOL=r[n> <ABSGCONV=r[n] | ABSGTOL=r[n> ...;

	OUTLIER <ALPHA=significance-level> <MAXNUM=number> <MAXPCT=number> ...;

	RANDOMREG <NOEST> <PLOT=<FILTER | SMOOTH | (<FILTER> <SMOOTH>)>> <PRINT=<FILTER | SMOOTH | (<FILTER> <SMOOTH>)>> ...;

	SEASON <DROPHARMONICS|DROPH=number-list | n TO m BY p> <KEEPHARMONICS|KEEPH=number-list | n TO m BY p> <LENGTH=integer> ...;

	SLOPE <NOEST> <PLOT=<FILTER | SMOOTH | (<FILTER> <SMOOTH>)>> <PRINT=<FILTER | SMOOTH | (<FILTER> <SMOOTH>)>> ...;

	SPLINEREG <DEGREE=integer> <KNOTS=number-list | n TO m BY p> <NKNOTS=m> ...;

	SPLINESEASON <DEGREE=integer> <KNOTS=integer-1 integer-2 ...> <LENGTH=integer> ...;
