PROC UNIVARIATE <ALL> <ALPHA=α> <ANNOTATE=SAS-data-set> <CIBASIC <(<TYPE=keyword> <ALPHA=>)>> <CIPCTLDF <(<TYPE=keyword> <ALPHA=>)>> <CIPCTLNORMAL <(<TYPE=keyword> <ALPHA=>)>> <DATA=SAS-data-set> <EXCLNPWGT> <FREQ> <GOUT=graphics-catalog> <IDOUT> <LOCCOUNT> <MODES> <MU0=values> <NEXTROBS=n> <NEXTRVAL=n> <NOBYPLOT> <NOPRINT> <NORMAL> <NOTABCONTENTS> <NOVARCONTENTS> <OUTTABLE=SAS-data-set> <PCTLDEF=1 | 2 | 3... > <PLOTSIZE=n> <PLOTS> <ROBUSTSCALE> <ROUND=units> <SUMMARYCONTENTS='string'> <TRIMMED=values <(<TYPE=keyword> <ALPHA=>)>> <VARDEF=DF | N | WDF... > <WINSORIZED=values <(<TYPE=keyword> <ALPHA=>)>>;     
	BY <DESCENDING> <NOTSORTED> ; 

	CDFPLOT <ALPHADELTA=value> <ALPHAINITIAL=value> <ANNOKEY> ...; 

	CLASS <KEYLEVEL=value | ( value1 value2 )> <MISSING> <ORDER=<DATA | FORMATTED | FREQ>... > ...; 

	FREQ variable;     
	HISTOGRAM <ALPHADELTA=value> <ALPHAINITIAL=value> <ANNOKEY> ...; 

	ID variable(s);     
	INSET <CFILL=color | BLANK> <CFILL=BLANK> <DATA=SAS-data-set> ...; 

	OUTPUT <OUT=SAS-data-set> <PCTLPRE=prefixes> <PCTLPTS=percentiles> ...; 

	PPPLOT <ALPHADELTA=value> <ALPHAINITIAL=value> <ANNOKEY> ...; 

	PROBPLOT <ALPHADELTA=value> <ALPHAINITIAL=value> <ANNOKEY> ...; 

	QQPLOT <ALPHADELTA=value> <ALPHAINITIAL=value> <ANNOKEY> ...; 

	VAR variable(s);     
	WEIGHT variable;
