PROC VARIOGRAM <DATA=SAS-data-set> <NOPRINT> <OUTACWEIGHTS=SAS-data-set> <OUTDISTANCE=SAS-data-set> <OUTPAIR=SAS-data-set> <OUTVAR=SAS-data-set> <PLOTS=ALL | EQUATE | FITPLOT|FIT... >;     
	BY <DESCENDING> <NOTSORTED> ; 

	COMPUTE <ALPHA=number> <ANGLETOLERANCE=angle tolerance> <AUTOCORRELATION <(autocorrelation-options)> Experimental> ...;     
	COORDINATES <XCOORD= (variable-name)> <YCOORD= (variable-name)> ; 

	DIRECTIONS directions-list ;     
	ID variable ;     
	MODEL ALPHA=number CHOOSE=AIC|SSE|STATUS  CL ...;     
	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r <n> | ABSFTOL=r<n>> <ABSGCONV= | ABSGTOL=r <n>> ...;     
	PARMS <HOLD=value-list> <LOWERB=value-list> <MAXSCALE=maxscale> ...; 

	STORE <LABEL=store-label> OUT=store-name ;     
	VAR analysis-variables-list ;
