statements.help:
PROC VARMAX <COVOUT> <DATA=SAS-data-set> <OUTCOV> <OUTEST=SAS-data-set> <OUTSTAT=SAS-data-set> <PLOTS=ALL | FORECASTS<(forecasts-plot-options)> | IMPULSE<(impulse-plot-options)>... >;     
	BY <DESCENDING> <NOTSORTED> ;

	CAUSAL <GROUP1=(variables)> <GROUP2=(variables)> ;

	COINTEG <EXOGENEITY> <H=(matrix)> <J=(matrix)> ...;

	GARCH <FORM=<BEKK | CCC>> <OUTHT=SAS-data-set> <P=number | P=(number-list)> ...;

	ID <ALIGN=<BEGINNING | MIDDLE | ENDING>> <INTERVAL=<DAY | HOUR | MINUTE>... > ;

	MODEL <CENTER> <DIF(variable (number-list) <... variable (number-list)>)> <DIFX(number-list) | DIFX=(number-list)> ...;

	NLOPTIONS <ABSCONV= | ABSTOL=r> <ABSFCONV=r[n] | ABSFTOL=r[n> <ABSGCONV=r[n] | ABSGTOL=r[n> ...;

	OUTPUT <ALPHA=number> <BACK=number> <LEAD=number> ...;

	RESTRICT <AR(l,i,j)=> <CONST(i)=> <MA(l,i,j)=> ...;

	TEST <AR(l,i,j)=> <CONST(i)=> <MA(l,i,j)=> ...;
