PROC X12 <AUXDATA=SAS-data-set> <DATA=SAS-data-set> <DATE=variable | DATEVAR=variable> <INEVENT=SAS-data-set> <INTERVAL=DAY | HOUR | MINUTE... > <MDLINFOIN=SAS-data-set> <MDLINFOOUT=SAS-data-set> <NOPRINT> <NOTRIMMISS> <OUTSTAT=SAS-data-set> <PERIODOGRAM> <PLOTS=ALL | NONE | SERIES... > <SEASONS=number> <SPAN=(mmmyy ,mmmyy ) | SPAN=(’yyQq’ ,’yyQq’ )> <SPECTRUMSERIES=table-name> <START=mmmyy | START=’yyQq’ | STARTDATE=mmmyy | STARTDATE=’yyQq’>;     
	ADJUST <PREDEFINED=<LOM | LOQ | LPYEAR>> ; 

	ARIMA <MODEL=((p d q) (P D Q)s)> ; 

	AUTOMDL <DIFFORDER=(nonseasonal order, seasonal order)> <MAXDIFF=(nonseasonal order, seasonal order)> <MAXORDER=(nonseasonal order, seasonal order)> ...;     
	BY variables ;     
	CHECK <MAXLAG=value> <PRINT=<ACF | PACF | ACFSQUARED>... > ; 

	ESTIMATE <ITPRINT> <MAXITER=value> <TOL=value> ...; 

	EVENT <B=(value <F> ...)> <USERTYPE=<AO | CONSTANT | EASTER>... > ; 

	FORECAST <LEAD=value> ; 

	ID variables ;     
	IDENTIFY <DIFF=(order, order, order)> <MAXLAG=value> <SDIFF=(order, order, order)> ...;     
	INPUT <B=(value <F> ...)> <USERTYPE=<AO | CONSTANT | EASTER>... > ; 

	OUTLIER <CV=value> <SPAN=(mmmyy ,mmmyy ) | SPAN=(’yyQq’ ,’yyQq’ )> <TYPE=<NONE | (AO LS TC) | (AO LS)>... > ...; 

	OUTPUT <A1> <A2> <OUT=SAS-data-set> ...; 

	REGRESSION <B=(value <F> ...)> PREDEFINED=CONSTANT< / B= >|LOM|LOMSTOCK...  USERVAR=(variables) < / B=value USERTYPE=option> ...;     
	TABLES <A19> <C20> <NOSUMMARYLINE> ...; 

	TRANSFORM <FUNCTION=<NONE | LOG | SQRT>... > <POWER=value> ; 

	USERDEFINED variables ;     
	VAR variables ;     
	X11 <MODE=<ADD | MULT | LOGADD>... > <OUTFORECAST> <SEASONALMA=<S3X1 | S3X3 | S3X5>... > ...;
